import React from 'react';

const Sidebar = () => {
  return (
    <div className="w-64 bg-indigo-800 text-white flex flex-col items-center py-6">
      <h1 className="text-3xl font-bold">CourseGPT</h1>
      <p className="mt-2 text-sm">Your Professional Learning Platform</p>
    </div>
  );
};

export default Sidebar;
