import React, { useState, useRef } from 'react';
import axios from 'axios';
import ReCAPTCHA from 'react-google-recaptcha';

const Signup = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const recaptchaRef = useRef(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Reset reCAPTCHA to ensure a fresh token
    recaptchaRef.current.reset();
    
    // Generate a fresh token
    let recaptchaToken;
    try {
      recaptchaToken = await recaptchaRef.current.executeAsync();
      console.log('Fresh reCAPTCHA token generated:', recaptchaToken);
    } catch (err) {
      console.error('Error generating reCAPTCHA token:', err);
      setError('Failed to generate reCAPTCHA token');
      return;
    }

    if (!recaptchaToken) {
      setError('Please complete the reCAPTCHA');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/auth/signup', {
        email,
        password,
        recaptchaToken,
      });

      const { token } = response.data;
      console.log('Signup successful, received token:', token);
      onLogin(token);
    } catch (err) {
      console.error('Signup error:', err.response?.data);
      setError(err.response?.data?.msg || 'Signup failed');
      recaptchaRef.current.reset(); // Reset reCAPTCHA after failure
    }
  };

  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-4 text-center">CourseGPT - Sign Up</h2>
      {error && <p className="text-red-500 text-center mb-4">{error}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          className="w-full p-2 mb-4 border border-gray-300 rounded"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          className="w-full p-2 mb-4 border border-gray-300 rounded"
          required
        />
        <div className="mb-4 flex justify-center">
          <ReCAPTCHA
            ref={recaptchaRef}
            sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
            size="normal" // Use "normal" for visible reCAPTCHA, "invisible" for executeAsync
          />
        </div>
        <button
          type="submit"
          className="w-full p-2 bg-green-500 text-white rounded hover:bg-green-600"
        >
          Sign Up
        </button>
      </form>
      <p className="mt-4 text-center">
        Already have an account?{' '}
        <a href="/login" className="text-blue-500 hover:underline">
          Login
        </a>
      </p>
    </div>
  );
};

export default Signup;
