import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import axios from 'axios';
import LessonGenerator from './components/LessonGenerator';
import ModuleOrganizer from './components/ModuleOrganizer';
import Login from './components/Login';
import Signup from './components/Signup';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    console.log('Token found in localStorage:', token); // Debug token
    if (token) {
      axios.defaults.headers.common['x-auth-token'] = token;
      setIsAuthenticated(true);
      console.log('User is authenticated, redirecting to /lesson');
    } else {
      console.log('No token found, user is not authenticated');
    }
  }, []);

  const handleLogin = (token) => {
    console.log('Logging in with token:', token); // Debug login
    localStorage.setItem('token', token);
    axios.defaults.headers.common['x-auth-token'] = token;
    setIsAuthenticated(true);
    navigate('/lesson');
  };

  const handleLogout = () => {
    console.log('Logging out'); // Debug logout
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['x-auth-token'];
    setIsAuthenticated(false);
    navigate('/login');
  };

  console.log('Current authentication state:', isAuthenticated); // Debug state

  return (
    <div className="container mx-auto p-4">
      <nav className="flex justify-between mb-6">
        <h1 className="text-3xl font-bold">CourseGPT</h1>
        {isAuthenticated && (
          <button
            onClick={handleLogout}
            className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
          >
            Logout
          </button>
        )}
      </nav>
      <Routes>
        <Route
          path="/login"
          element={
            isAuthenticated ? (
              <Navigate to="/lesson" />
            ) : (
              <Login onLogin={handleLogin} />
            )
          }
        />
        <Route
          path="/signup"
          element={
            isAuthenticated ? (
              <Navigate to="/lesson" />
            ) : (
              <Signup onLogin={handleLogin} />
            )
          }
        />
        <Route
          path="/lesson"
          element={
            isAuthenticated ? (
              <LessonGenerator />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/module"
          element={
            isAuthenticated ? (
              <ModuleOrganizer />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route path="/" element={<Navigate to={isAuthenticated ? "/lesson" : "/login"} />} />
      </Routes>
    </div>
  );
};

export default App;
